SELECT
OBJECTID AS email_id,
OBJECTTYPEID AS email_type,
UPDATEDAT AS email_update_date,
PROPERTY_HS_CREATED_BY AS email_created_by,
PROPERTY_HS_CREATEDATE AS email_created_date,
PROPERTY_UNPARSED_HS_CREATEDATE AS email_created_timestamp,
PROPERTY_HS_EMAIL_FROM_EMAIL AS email_from_email,
PROPERTY_HS_EMAIL_HTML AS email_html,
PROPERTY_HS_EMAIL_SENT_VIA AS email_sent_via,
PROPERTY_HS_EMAIL_STATUS AS email_status,
PROPERTY_HS_EMAIL_SUBJECT AS email_subject,
PROPERTY_HS_EMAIL_TEXT AS email_text,
PROPERTY_HS_EMAIL_TO_EMAIL AS email_to_email,
PROPERTY_HS_ENGAGEMENT_TYPE AS email_engagement_type,
PROPERTY_HS_LASTMODIFIEDDATE AS email_last_modified_date,
PROPERTY_UNPARSED_HS_LASTMODIFIEDDATE AS email_last_modified_timestamp,
PROPERTY_HUBSPOT_OWNER_ID AS email_hubspot_owner_id,
PROPERTY_HUBSPOT_TEAM_ID AS email_hubspot_team_id
FROM HUBSPOT_DB.V2_LIVE.OBJECTS_ENGAGEMENTS
WHERE PROPERTY_HS_ENGAGEMENT_TYPE = 'EMAIL'
AND EMAIL_CREATED_DATE >= '2023-01-01'
ORDER BY EMAIL_CREATED_DATE 
LIMIT 2000
